import React, { useEffect, useState } from 'react';
import Layout from '../../Component/Layout'
import moment from 'moment';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import './style.scss'
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { InputTextarea } from 'primereact/inputtextarea';
import './style.scss'
import { SplitButton } from 'primereact/splitbutton';
function Cart() {

    const [data, setData] = useState([])
    const [sumPrice , setSumPrice] = useState(0)

    useEffect(() => {
        if (localStorage.getItem("userId")) {
            loadCart()
        }
    }, [])

    const loadCart = () => {
        fetch("http://localhost:8080/cart/get-by-user", {
            method: "POST",
            headers: { "Content-Type": "application/x-www-form-urlencoded" },
            body: new URLSearchParams({
                'userId': Number(localStorage.getItem("userId")),
            })
        }).then((res) => res.json()).then((result) => {
            if (result) {

                if (result.status) {


                } else {
                    var sum = 0;
                    setData(result)
                    for (let index = 0; index < result.length; index++) {
                        sum += result[index].price;
                        
                    }
                    setSumPrice(sum)
                }
            }
        })
    }
    const header = (
        <div className="c-header-table">
            <span className="text-xl text-900 font-bold">Giá» hÃ ng</span>
            <span className="text-xl text-900 font-bold">Tá»ng tiá»n pháº£i thanh toÃ¡n: {sumPrice}VNÄ</span>
            <Button label='Thanh toÃ¡n'/>
        </div>
    );

    const renderImg = (rowData) => {
        return <img src={`${rowData.image}`} style={{ maxWidth: "100px" }} />
    }

    const remove = (rowData) => {
        fetch("http://localhost:8080/cart/delete", {
            method: "POST",
            headers: { "Content-Type": "application/x-www-form-urlencoded" },
            body: new URLSearchParams({
                'id': rowData.id,
            })
        }).then((res) => {

            if (!res.ok) {


            } else {
                loadCart()
            }

        })
    }

    const renderLasterColumn = (rowData) => {
        const items = [
            {
                label: 'Thanh toÃ¡n',
                icon: 'pi pi-refresh',
                command: () => { }
            },
            {
                label: 'Há»§y',
                icon: 'pi pi-times',
                command: () => {
                    remove(rowData)
                }
            },

        ];
        return (
            <div className="card flex justify-content-center">
                <SplitButton model={items} className='c-custom-btn'/>
            </div>
        )
    }

    return (
        <Layout>
            <div style={{ padding: '2%' }}>
                <DataTable header={header} value={data} tableStyle={{ minWidth: '60rem' }}>
                    <Column field="productName" header="TÃªn sáº£n pháº©m"></Column>
                    <Column field="productCode" header="MÃ£ sáº£n pháº©m"></Column>
                    <Column body={renderImg} header="áº¢nh"></Column>
                    <Column field="productColorName" header="MÃ u sáº£n pháº©m"></Column>
                    <Column field="productSizeName" header="Cá»¡ sáº£n pháº©m"></Column>
                    <Column field="trademark" header="ThÆ°Æ¡ng hiá»u"></Column>
                    <Column field="quantityProduct" header="Sá» lÆ°á»£ng"></Column>
                    <Column body={(rowData) => { return <p>{rowData.price / rowData.quantityProduct} VND</p> }} header="ÄÆ¡n giÃ¡"></Column>
                    <Column body={(rowData) => { return <p>{rowData.price} VND</p> }} header="Tá»ng tiá»n"></Column>
                    <Column body={renderLasterColumn} header="Tá»ng tiá»n"></Column>
                </DataTable>
            </div>
        </Layout>

    );
}

export default Cart;
