import { Component } from 'react';
import {NavLink} from 'react-router-dom'
import './Nav.scss'

class Navigation extends Component {
    render() {
        return (
            <>
                <ul>
                    <li>
                    <NavLink to="/comp1" className={({isActive}) => (isActive ? "active-style" : 'background-color: "red"')}>Comp1</NavLink>
                    </li>
                    <li>
                    <NavLink to="/comp2" className={({isActive}) => (isActive ? "active-style" : 'background-color: "red"')}>Comp2</NavLink>
                    </li>
                    <li>
                    <NavLink to="/user" className={({isActive}) => (isActive ? "active-style" : 'background-color: "red"')}>List_User</NavLink>
                    </li>
                </ul>
            </>
        )
    }
}

export default Navigation;
