import React, { Component } from 'react';
import Comp1 from './Comp1';
import Comp2 from './Comp2';

class CompParent extends Component {
    state = {
        data: 0
    }
    handleClick = () => {
        log
        this.setState = {
            data: Math.random()
        }
    }
    render() {
        console.log("call re-render parent: ", this.state.data);
        return (
            <>
                <button onClick={() => { this.handleClick() }}>click to change parent state</button>
                <Comp1 data={this.state.data} />
                <Comp2 rawData={"raw"} />
            </>
        );
    }
}

export default CompParent;
